mapper.select:
select * from p_user_balance_log 


		 user_id = #{userId}


		 AND balance_type = #{balanceType} 


		 AND order_id = #{orderId} 


		 AND trade_type = #{tradeType} 


		 AND subject = #{subject} 


		 AND sub_subject = #{subSubject} 


		 AND status = #{status} 


ORDER BY id DESC 
 LIMIT #{start} , #{size}

select count(*) as num from p_user_balance_log 


		 user_id = #{userId}  


		 AND balance_type = #{balanceType} 


		 AND order_id = #{orderId} 


		 AND trade_type = #{tradeType} 


		 AND subject = #{subject} 


		 AND sub_subject = #{subSubject} 


		 AND status = #{status}
